import React, {useContext} from 'react';
import Context from '../store/context';
import Table from 'react-bootstrap/Table';
import ShoppingCartTitle from '../Images/ShoppingCartTitle.png';
import "bootstrap/dist/css/bootstrap.css";
import UserContext from '../store/context';
import LinkContainer from 'react-router-bootstrap';
import { Navbar, Nav, NavDropdown, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Checkout from '../Pages/Checkout';

function ShoppingCart () {
    const context = useContext(UserContext);
    var cartLength = context.cart.length;
    // var totalPrice = cartLength * 3;

    function remove(id) {
        context.cart.splice(id - 1, 1);
        context.setCart(context.cart);
        var row = document.getElementById(id);
        row.parentNode.removeChild(row);
        context.setCartTotal(context.cartTotal - 3);
        var totalPriceLabel = document.getElementById("total-price");
        totalPriceLabel.innerHTML = "$" + context.cartTotal + ".00";
    }

    return (
        <div className="custom-text">
            <h1><img id="prices-title" src={ShoppingCartTitle} alt="Prices"></img></h1>

            <Table striped bordered hover variant="none" id="prices-table">
                <thead>
                    <tr style={{fontSize: "larger"}}>
                        <th>ID#</th>
                        <th>Item</th>
                        <th>Price</th>
                        <th>Remove</th>
                    </tr>
                    {
                      cartLength != 0 ?
                        context.cart.map( (item) => {
                          return (
                            <tr id={item.id}>
                              <td>{item.id}</td>
                              <td>{item.name}</td>
                              <td>$3.00</td>
                              <td><button onClick={() => remove(item.id)}>remove</button></td>
                            </tr>
                          )
                        }):
                        <tr></tr>
                    }
                    <tr>
                        <th>Total:</th>
                        <td></td>
                        <th id="total-price">${context.cartTotal}.00</th>
                    </tr>
                </thead>
            </Table>
            {
              cartLength != 0 ?
                <Link to={Checkout}>
                  <Button style={{marginTop: "3vh", fontSize: "2rem", alignSelf: "center", background: "black", color: "white", boxShadow: "1px 1px 12px black", borderRadius: "20px"}}>Checkout</Button>
                </Link>:
                // <button style={{marginTop: "3vh", fontSize: "2rem", alignSelf: "center"}}>Checkout</button>:
                <h3>Cart is Empty</h3>
            }
        </div>
    );
}

export default ShoppingCart;
