import React from 'react'
import { Button, FormGroup, Label, Input, FormText, Container } from 'reactstrap';
import FormControl from 'react-bootstrap-forms';
import "bootstrap/dist/css/bootstrap.css";

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {userName: '', password: '', loggedIn: false};
    }

    emailChange(event) {
        this.setState({userName: event.target.value})
    }

    passwordChange(event) {
        this.setState({password: event.target.value})
    }

    loginClicked() {
        this.state.loggedIn = true;
        document.getElementById("login-form").classList.add("hide");
        var confirmLoginDiv = document.createElement("div");
        var welcomeLine = document.createElement("h4");
        welcomeLine.innerHTML = "Welcome, " + this.state.userName + "!";
        var userInfo = document.createElement("h5");
        userInfo.innerHTML = (
            "You registered with the following information:" +
            "Username: " + this.state.userName + ", " +
            "Password: " + this.state.password
        );
        confirmLoginDiv.appendChild(welcomeLine);
        confirmLoginDiv.appendChild(userInfo);

        document.getElementById("login").appendChild(confirmLoginDiv);
    }

    render() {
        return (
            <Container className="custom-text" id="login">
            <h1 id="login-title">Login:</h1>
            <FormGroup id="login-form">
                <h4>Enter username and password below to login!</h4>
                <br></br>
                <FormGroup>
                    <Label for="exampleName"><h4>Username</h4></Label>
                    <Input type="name" name="name" id="exampleName" placeholder="Username" onChange={(item) => this.emailChange(item)} value={this.state.userName} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleEmail"><h4>Password</h4></Label>
                    <Input type="password" name="password" id="password" placeholder="Password" onChange={(item) => this.passwordChange(item)} value={this.state.password} />
                </FormGroup>
                <Button onClick={() => this.loginClicked()}>Login</Button>
            </FormGroup>
            </Container>
        );
    }
}

export default Login;
