    function getIndex(id) {
      return context.cart.findIndex(obj => obj.id === id);
    }

    return (
        <div className="custom-text">
            <h1><img id="prices-title" src={ShoppingCartTitle} alt="Prices"></img></h1>

            <Table striped bordered hover variant="none" id="prices-table">
                <thead>
                    <tr style={{fontSize: "larger"}}>
                        <th>ID#</th>
                        <th>Item</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Remove</th>
                    </tr>
                    {
                      cartLength != 0 ?
                        context.cart.map( (item) => {
                          return (
                            <tr id={item.id}>
                              <td>{item.id}</td>
                              <td>{item.name}</td>
                              <td>{item.quantity}</td>
                              <td>$3.00</td>
                              <td><button onClick={() => remove(item.id)}>remove</button></td>
                            </tr>
                          )
                        }):
                        <tr></tr>
                    }
                    <tr>
                        <th>Total:</th>
                        <td></td>
                        <th id="total-price">${context.cartTotal}.00</th>
                    </tr>
                </thead>
            </Table>
            {
              cartLength != 0 ?
                <Button style={{marginTop: "3vh", fontSize: "2rem", alignSelf: "center", background: "black", color: "white", outline: "black", boxShadow: "1px 1px 12px black", borderRadius: "20px"}}>Checkout</Button>:
                // <button style={{marginTop: "3vh", fontSize: "2rem", alignSelf: "center"}}>Checkout</button>:
                <h3>Cart is Empty</h3>
            }
        </div>
    );
}

export default ShoppingCart;
