import React, { useContext } from 'react'
import render from "react-dom";
import { Card, Button } from 'react-bootstrap';
import UserContext from '../store/context';

const context = useContext(UserContext);


export default class Sticker extends React.Component {
    constructor(props) {
        super(props);
        this.handleAddToCart = this.addToCart.bind(this);
        this.handleDetailsPage = this.viewDetails.bind(this);
    }

    addToCart(id) {
        context.cart.push({id: this.props.id, name: this.props.name, description: this.props.description, image: this.props.image});
    }

    viewDetails(id) {
        var currentCard = document.getElementById("sticker-card");

    }
    render() {
        const id = this.props.id;
        return (
            <Card className="sticker-card">
                <Card.Img variant="top" src={this.props.image} />
                <Card.Body>
                    <Card.Title><h3>{this.props.name}</h3></Card.Title>
                    <Card.Text>{this.props.description}</Card.Text>
                    {/* <Button variant="primary" onClick={this.handleAddToCart}>Add To Cart</Button> */}
                    <Button variant="primary" onClick={this.handleDetailsPage}>Add To Cart</Button>
                </Card.Body>
            </Card>
        );
    }
}



