import React, {useContext} from 'react';
import Context from '../store/context';
import Table from 'react-bootstrap/Table';
import ShoppingCartTitle from '../Images/ShoppingCartTitle.png';
import "bootstrap/dist/css/bootstrap.css";
import UserContext from '../store/context';

function ShoppingCart () {
    const context = useContext(UserContext);
    var cartLength = context.cart.length;

    function remove(id) {
        delete context.cart[id - 1];
        context.setCart(context.cart);
        var row = document.getElementById(id);
        row.parentNode.removeChild(row);
    }

    return (
        <div className="custom-text">
            <h1><img id="prices-title" src={ShoppingCartTitle} alt="Prices"></img></h1>

            <Table striped bordered hover variant="none" id="prices-table">
                <thead>
                    <tr style={{fontSize: "larger"}}>
                        <th>ID#</th>
                        <th>Item</th>
                        <th>Price</th>
                        <th>Remove</th>
                    </tr>
                    {
                      cartLength != 0 ?
                        context.cart.map( (item) => {
                          return (
                            <tr id={item.id}>
                              <td>{item.id}</td>
                              <td>{item.name}</td>
                              <td>$3.00</td>
                              <td><button onClick={remove(item.id)}>remove</button></td>
                            </tr>
                          )
                        }):
                        <tr></tr>
                    }
                    <tr>
                        <th>Total:</th>
                        <td></td>
                        <th>${context.cart.length * 3}.00</th>
                    </tr>

                    {/* <tr>
                        <td>1</td>
                        <td>Pre-Designed Sticker</td>
                        <td>Small: $4.00</td>
                    </tr>
                    <tr>
                        <td>2</td>
                        <td></td>
                        <td>Medium: $6.00</td>
                    </tr>
                    <tr>
                        <td>3</td>
                        <td></td>
                        <td>Large: $10.00</td>
                    </tr>
                    <tr>
                        <td>4</td>
                        <td>Custom Image Sticker</td>
                        <td>Small: $6.00</td>
                    </tr>
                    <tr>
                        <td>5</td>
                        <td></td>
                        <td>Medium: $8.00</td>
                    </tr>
                    <tr>
                        <td>6</td>
                        <td></td>
                        <td>Large: $13.00</td>
                    </tr> */}
                </thead>
            </Table>
        </div>
    );
}

export default ShoppingCart;
