import React, {useContext} from 'react'
import Context from '../store/context';
import { Button, FormGroup, Label, Input, FormText, Container } from 'reactstrap';
import FormControl from 'react-bootstrap-forms';
import "bootstrap/dist/css/bootstrap.css";

const Login = () => {
    const {state, actions} = useContext(Context);

    function loginClicked() {
        // document.getElementById("login-form").classList.add("hide");
        var confirmLoginDiv = document.createElement("div");
        var welcomeLine = document.createElement("h4");
        welcomeLine.innerHTML = "Welcome, " + state.value + "!";
        var userInfo = document.createElement("h5");
        userInfo.innerHTML = (
            "You registered with the following information:" +
            "Username: " + this.state.value + ", " +
            "Password: " + this.state.value
        );
        confirmLoginDiv.appendChild(welcomeLine);
        confirmLoginDiv.appendChild(userInfo);

        document.getElementById("login").appendChild(confirmLoginDiv);
    }

    return (
        <Container className="custom-text" id="login">
            <h1 id="login-title">Login:</h1>
            <FormGroup id="login-form">
                <h4>Enter username and password below to login!</h4>
                <br></br>
                <FormGroup>
                    <Label for="exampleName"><h4>Username</h4></Label>
                    <Input  value={state.value} onChange={(e) => actions({type: 'setState', payload: {...state, value: e.target}})} type="text" />
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword"><h4>Password</h4></Label>
                    <Input  value={state.value} onChange={(e) => actions({type: 'setState', payload: {...state, value: e.target}})} type="password" />
                </FormGroup>
                <Button onClick={() => loginClicked()}>Login</Button>
            </FormGroup>
        </Container>
    )
}

